export default {
    components: { Carousel, Slide },
    data: function () {
        return {
            slideNum: [0,false],
            isLoad: false,
        }
    },
    methods: {
        gotoSlide(num) {
            this.slideNum = [num, false]
        },
        isSelected(num) {
            return this.slideNum[0] == num
        }
    },
    mounted: function() {
        this.$nextTick(function () {
            this.isLoad = true
        })
    }
};

